import { useQuery } from "@apollo/client";
import { Avatar, Grid, Typography } from "@mui/material";
import React from "react";
import { useParams } from "react-router-dom";
import { GET_PET } from "./queries";

export const PetDetail = () => {
  const { id } = useParams();

  const { data, loading, error } = useQuery(GET_PET, {
    variables: { petId: id },
  });

  if (error) return "errorrrrr";

  if (loading) return "loading...";

  const pet = data.pet;
  return (
    <>

    <Grid container spacing={4}>
      <Grid item xs={2}>
      <Avatar alt={pet.petname} src={pet.petphoto} sx={{ width: 100, height: 200 }}/>
        <Typography variant="h4">{pet.petname}</Typography>
      </Grid>
      <Grid item xs={3}>
        <Typography variant="h5">{pet.petname}</Typography><Typography>is a fun loving {pet.pettype}!</Typography>
      </Grid>
    </Grid>
    <br></br>
    <Typography>You can take {pet.petname} home today for ${pet.petprice}!</Typography>
    </>
  );
};
